import React from 'react'

const Country = ({ name }) => {
    return (
        <li>
            {name}
        </li>
    )
}

const soloCountry = ({ single }) => {
    return (
        <>
            <h1>{single.name}</h1>
            <p>capital {single.capital}</p>
            <p>population {single.population}</p>
            <h3>languages</h3>
            <ul>
                {props.languages.map(l => <Language language={l} />)}
            </ul>
            <img src={single.flag}></img>
        </>
    )
}

const Language = ({ language }) => {
    return (
        <li>
            {language.nativeName}
        </li>
    )
}

const Countries = ({ result }) => {
    if (result.length > 10) {
        return (
            <p>Too many matches, specify another filter</p>
        )
    }
    else if (result.length === 1) {
        return (
            <soloCountry single={result[0]} />
        )
    }

    return (
        <ul>
            {result.map(c => <Country key={c.area} name={c.name} />)}
        </ul>
    )
}

export default Countries;
